import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { NavLink } from 'react-router-dom'
function ProductoCard({producto}) {
  return (
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={producto.imagen} />
      <Card.Body>
      <Card.Title>
         <NavLink to={`/producto/${producto.id}`} >
          {producto.nombre}</NavLink></Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          Marca: {producto.marcaId.nombre}<br></br>
          Categoria: {producto.categoriaId.nombre}<br></br>
          Unidad de Medida: {producto.unidadMedidaId.nombre}
          </Card.Subtitle>
        
        <Button variant="primary">Modificar</Button>
      </Card.Body>
    </Card>
  );
}

export {ProductoCard};
