import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import {  useState } from 'react';
function CrearInventarioFormulario({callback}) {
    const [cantidadStock,setCantidadStock]=useState("")
    const [cantidadMaxima,setCantidadMaxima]=useState("")
    const [cantidadMinima,setCantidadMinima]=useState("")
    const [precioCosto,setPrecioCosto]=useState("")
    const [precioVenta,setPrecioVenta]=useState("")
    const [productoId,setProductoId]=useState("")
  
    const enviarFormulario=(e)=>{
      e.preventDefault()
      callback({cantidadStock,cantidadMaxima,cantidadMinima,precioCosto,precioVenta,productoId:{id:productoId}}) 
      
    }
    
      return (
        <Form onSubmit={enviarFormulario}>
          <Form.Group className="mb-3" controlId="stock">
            <Form.Label>Cantidad en Stock </Form.Label>
            <Form.Control type="text" placeholder="ingrese marca de producto" required
             value={cantidadStock} onChange={e=>setCantidadStock(e.target.value)} /> 
          </Form.Group>
    
         
        
    
    
    
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      );
    }
    
    export {CrearInventarioFormulario}
