import axios from "axios";
import Button from "react-bootstrap/esm/Button";
import { useNavigate } from "react-router-dom"; 

const EliminarProductoBoton=({id,titulo})=>{
    const navegar=useNavigate()
    const eliminar= async()=>{
        axios.delete(`${ELIMINARPRODUCTO_DELETE_ENDPOINT}/${id}`)
        .then (respuesta=>{
            navegar('/')
        })
        .catch(err=>{
            console.error(err)
        })
        }
const crearAlerta=()=>{
    const titulo=`Eliminar producto \n ¿desea eliminar el producto ${titulo}?`
    return (window.confirm(titulo)==true) ? eliminar():()=>{}
}
return (
    <Button variant="danger" size="sm" onClick={crearAlerta}>Eliminar </Button>
)

}

export {EliminarProductoBoton}
