import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import { PRODUCTOSCREADOS_GET_ENDPOINT } from '../connections/endpoints';
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useParams } from 'react-router-dom';



function ProductosDetalle() {
    let [producto,setProducto]=useState([])
    const {id}=useParams()
    useEffect(()=>{
        

        axios.get(`${PRODUCTOSCREADOS_GET_ENDPOINT}/${id}`)
        .then(respuesta=>{
        
        setProducto(respuesta.data)
        producto=respuesta.data
        console.log(producto)
        })
        .catch(err=>{
        console.error(err)
        })

    },[id])

  return (
    <Container className='mt-3 mb-3 text-center'>
      
  <h3>Crear Marca</h3>
  <Row className="justify-content-center">
    <Col sm="12">
    <Row className="justify-content-center">
        <CrearInventarioFormulario callback={crearInventario}/>
        </Row>
    </Col>
  </Row><br></br>
        <h3>Productos</h3>
      <Row className="justify-content-center">
        <Col sm="12">
        <Row className="justify-content-center">
        {producto}
            </Row>
        </Col>
      </Row>
    </Container>
  );
}

export {ProductosDetalle};
