import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function ProductoCard({producto}) {
  return (
    <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={producto.productoId.imagen} />
      <Card.Body>
      <Card.Title>
         
          {producto.productoId.nombre}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          Marca: {producto.productoId.marcaId.nombre}<br></br>
          Categoria: {producto.productoId.categoriaId.nombre}<br></br>
          Unidad de Medida: {producto.productoId.unidadMedidaId.nombre}
          </Card.Subtitle>
        <Card.Text>
          Precio de venta: ${producto.precioVenta}<br></br>
          Costo: $ {producto.precioCosto}<br></br>
          Unidades Disponibles: {producto.cantidadStock}<br></br>
          Unidades Minimas: {producto.cantidadMinima}<br></br>
          Unidades Maximas: {producto.cantidadMaxima}
        </Card.Text>
        <Button variant="primary">Modificar</Button>
      </Card.Body>
    </Card>
  );
}

export {ProductoCard};
