import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import { PRODUCTOSCREADOS_GET_ENDPOINT } from '../connections/endpoints';
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useParams } from 'react-router-dom';



function ProductosDetalle() {
    const [producto,setProductos]=useState([])
    const {id}=useParams()
    useEffect(()=>{
        

        axios.get(`${PRODUCTOSCREADOS_GET_ENDPOINT}/${id}`)
        .then(respuesta=>{
        console.log(respuesta.data)
        setProductos(respuesta.data)
        })
        .catch(err=>{
        console.error(err)
        })

    },[id])

  return (
    <Container className='mt-3 mb-3 text-center'>
        <h3>Productos</h3>
      <Row className="justify-content-center">
        <Col sm="12">
        <Row className="justify-content-center">
        {producto.productoId.imagen}
            </Row>
        </Col>
      </Row>
    </Container>
  );
}

export {ProductosDetalle};
