import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import {  useState } from 'react';
import {  useEffect } from 'react';
import axios from 'axios';
import { PRODUCTOSCREADOS_GET_ENDPOINT } from '../connections/endpoints';

function CrearInventarioFormulario({callback}) {
    const [cantidadStock,setCantidadStock]=useState("")
    const [cantidadMaxima,setCantidadMaxima]=useState("")
    const [cantidadMinima,setCantidadMinima]=useState("")
    const [precioCosto,setPrecioCosto]=useState("")
    const [precioVenta,setPrecioVenta]=useState("")
    const [productoId,setProductoId]=useState("")
  
    const enviarFormulario=(e)=>{
      e.preventDefault()
      callback({cantidadStock,cantidadMaxima,cantidadMinima,precioCosto,precioVenta,productoId:{id:productoId}}) 
      
    }

    const [productos,setProductos]=useState([])
    useEffect(()=>{
        axios.get(PRODUCTOSCREADOS_GET_ENDPOINT)
        .then(respuesta=>{
        setProductos(respuesta.data)
        })
        .catch(err=>{
        console.error(err)
        })
    },[])
    
      return (
        <Form onSubmit={enviarFormulario} className="d-flex">
          <Form.Group className="mb-2 p-3" controlId="stock">
            <Form.Label>Cantidad en Stock </Form.Label>
            <Form.Control type="text" placeholder="c. stock" required
             value={cantidadStock} onChange={e=>setCantidadStock(e.target.value)} /> 
          </Form.Group>
          <Form.Group className="mb-2 p-3" controlId="cmaximo">
            <Form.Label>Cantidad Máxima </Form.Label>
            <Form.Control type="text" placeholder="c. maximo" required
             value={cantidadMaxima} onChange={e=>setCantidadMaxima(e.target.value)} /> 
          </Form.Group>
          <Form.Group className="mb-2 p-3" controlId="cminima">
            <Form.Label>Cantidad Mínima </Form.Label>
            <Form.Control type="text" placeholder="c. mínima" required
             value={cantidadMinima} onChange={e=>setCantidadMinima(e.target.value)} /> 
          </Form.Group>
          <Form.Group className="mb-2 p-3" controlId="costo">
            <Form.Label>Costo del producto </Form.Label>
            <Form.Control type="text" placeholder="ingrese costo del producto" required
             value={precioCosto} onChange={e=>setPrecioCosto(e.target.value)} /> 
          </Form.Group>
          <Form.Group className="mb-2 p-3" controlId="precio">
            <Form.Label>Precio de venta </Form.Label>
            <Form.Control type="text" placeholder="ingrese precio de venta" required
             value={precioVenta} onChange={e=>setPrecioVenta(e.target.value)} /> 
          </Form.Group>
          <Form.Group className="mb-2 p-6" controlId="productoID">
        <Form.Label>Producto</Form.Label>
        <Form.Control as="select" type="select" required
        value={productoId} onChange={e=>setProductoId(e.target.value)}>
           {productos.map(elemento=>
                <option value={elemento.id} key={elemento.id}>{elemento.nombre}</option>
            )}
        </Form.Control>
      </Form.Group>
    
         
        
    
    
    
          <Button variant="primary" type="submit" className='p-3'>
            Submit
          </Button>
        </Form>
      );
    }
    
    export {CrearInventarioFormulario}
