import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import { INVENTARIOSCREADOS_GET_ENDPOINT } from '../connections/endpoints';
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import { useParams } from 'react-router-dom';



function ProductosDetalle() {

        const [inventarios,setInventarios]=useState([])
        const {id}=useParams()
        useEffect(()=>{
        

          axios.get(`${INVENTARIOSCREADOS_GET_ENDPOINT}/${id}`)
        .then(respuesta=>{
        console.log(respuesta.data)
        setInventarios(respuesta.data)
        })
        .catch(err=>{
        console.error(err)
        
        })

    },[id])

   

  return (
    <Container className='mt-3 mb-3 text-center'>
      
        <h3>Detalles</h3>
      <Row className="justify-content-center">
        <Col sm="12">
        <Row className="justify-content-center">
      
            </Row>
            <Card style={{ width: '18rem' }}  >
      <Card.Img variant="top" src={inventarios.productoId.imagen} />
      <Card.Body>
      <Card.Title>
          {inventarios.productoId.nombre}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          Marca: {inventarios.productoId.marcaId.nombre}<br></br>
          Categoria: {inventarios.productoId.categoriaId.nombre}<br></br>
          Unidad de Medida: {inventarios.productoId.unidadMedidaId.nombre}
          </Card.Subtitle>
        
        <Button variant="primary">Modificar</Button>
      </Card.Body>
    </Card>
        </Col>
      </Row>
    </Container>
  );
}

export {ProductosDetalle};
