import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import axios from 'axios';
import { PRODUCTOSCREADOS_GET_ENDPOINT } from '../connections/endpoints';
import { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';



function ProductosDetalle({id}) {
    const [producto,setProductos]=useState([])
    useEffect(()=>{
        

        axios.get(PRODUCTOSCREADOS_GET_ENDPOINT+window.location.pathname-"/producto")
        .then(respuesta=>{
        console.log(respuesta.data)
        setProductos(respuesta.data)
        })
        .catch(err=>{
        console.error(err)
        })

    },[])
    

  return (
    <Container className='mt-3 mb-3 text-center'>
        <h3>Productos</h3>
      <Row className="justify-content-center">
        <Col sm="12">
        <Row className="justify-content-center">
        <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src={producto.productoId.imagen} />
      <Card.Body>
      <Card.Title>
          {producto.productoId.nombre}</Card.Title>
        <Card.Subtitle className="mb-2 text-muted">
          Marca: {producto.productoId.marcaId.nombre}<br></br>
          Categoria: {producto.productoId.categoriaId.nombre}<br></br>
          Unidad de Medida: {producto.productoId.unidadMedidaId.nombre}
          </Card.Subtitle>
        <Card.Text>
          Precio de venta: ${producto.precioVenta}<br></br>
          Costo: $ {producto.precioCosto}<br></br>
          Unidades Disponibles: {producto.cantidadStock}<br></br>
          Unidades Minimas: {producto.cantidadMinima}<br></br>
          Unidades Maximas: {producto.cantidadMaxima}
        </Card.Text>
        <Button variant="primary">Modificar</Button>
      </Card.Body>
    </Card>
            </Row>
        </Col>
      </Row>
    </Container>
  );
}

export {ProductosDetalle};
